mapper.update:
update user
set username =#{username},
    password=#{password}
where username=#{username};

mapper.select:
select username, password
from user
where username = #{username}
  and password = #{password};

select username, temperature, address, date, qr_code_color as qrCodeColor, is_to_dongguan as isToDongguan
from signinfo
where username = #{username};

SELECT * FROM signinfo
WHERE username = #{username}
  AND DATE_FORMAT(date, '%Y-%m-%d') = #{str}
