mapper.update:
update user
set username =#{username},
    password=#{password}
where username=#{username};

mapper.select:
select id as userid, username, password, `type` as authority, schoolid
from user
where username = #{username}
  and password = #{password};

select username, temperature, address, date, qr_code_color as qrCodeColor, is_to_dongguan as isToDongguan
from signinfo
where username = #{username};

select id as schoolid,schoolname
from school
where schoolname = #{schoolname};

mapper.update:
update tips
set content = #{content}
where id = #{id};

mapper.select:
SELECT * FROM signinfo
WHERE username = #{username}
  AND DATE_FORMAT(date, '%Y-%m-%d') = #{str};
